#!/bin/bashproject_name=gxbinfile_path=$(dirname $0)app_dir=$(cd ${binfile_path}/.. ; pwd)log_path=${app_dir}/${project_name}-`date +%F+%T`.logjar_name=gx-0.0.1-SNAPSHOT.jarjar_path=${app_dir}/${jar_name}# 停止脚本stop(){echo "正在停止服务..."pid=`ps -ef|grep ${jar_name} | grep -v grep | awk '{print $2}'`if [ -z "$pid" ]then   echo "程序未启动。"else   kill -9 $pid   echo "程序已停止。"fi}start(){echo "启动程序： "${project_name}nohup java -jar -Xms2048m -Xmx2048m -XX:MetaspaceSize=256m -XX:MaxMetaspaceSize=256m  ${jar_name} >> ${log_path} 2>&1 &if [ $? -eq 0 ] then   echo "启动成功！" else    echo "启动失败，请查看日志:"${log_path}fi}restart(){stopstart}case $1 in  "start")    start    ;;  "stop")    stop    ;;  "restart")    restart    ;;  *)    echo "Usage: launch [start | stop | restart] "	    ;;esac